import { getFeaturedProducts } from "../api/serverCalls";
const { createAsyncThunk, createSlice } =  require("@reduxjs/toolkit");


export const loadFeaturedProducts = createAsyncThunk(
    'featured/getData',
    async (type) => {
        const products = await getFeaturedProducts();
        return products
    }
) 

export const FeaturedSlice = createSlice({
    name: "featured",
    initialState: {
        products: [],
        gender: [],
        everydaySneakers: [],
        forRunners : [],
        isLoading:false,
        hasError:false
    },

    reducers: {},

    extraReducers: {

        [loadFeaturedProducts.pending]: (state, action) => {
            console.log('pending')
            state.isLoading = true;
            state.hasError = false;
        },

        [loadFeaturedProducts.fulfilled]: (state, action) => {
            console.log('fulfiled')           
            state.products = action.payload.featuredProducts;
            state.gender =  action.payload.gender;
            state.everydaySneakers = action.payload.everydaySneakersProducts;
            state.forRunners = action.payload.forRunnersProducts

            state.isLoading = false;
            state.hasError = false;
        },

        [loadFeaturedProducts.rejected]: (state, action) => {
            console.log('failed')
            state.isLoading = false;
            state.hasError = true;
        }
    }
});


export const selectdata = (state) => state.featured.products;

export const isLoadingData = (state) => state.featured.isLoading;
export default FeaturedSlice.reducer;
