import {React,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadHomeProducts } from "../slices/homeslice";
import Banner from "./banner";


function Home() {

    const dispatch = useDispatch();
    const featuredProducts = useSelector(selectfeatured);
    console.log('featured',featuredProducts)
    const gender = useSelector(selectgender);
    const everydaySneakers = useSelector(selecteverydaySneakers);
    const forRunners = useSelector(selectforRunners)


    useEffect(() => {
        dispatch(loadHomeProducts());
    }, 
    []);

    return(
    <>
        <Banner/>

        <div className="row my-4 mx-3 product_line">

        <h1 id="featured">Featured</h1>

            {featuredProducts.map(product => {
                return (
                <div class="col">
                    <a href="#"> <img src={process.env.PUBLIC_URL + product.image_path} class="img-fluid" alt="..."/> </a>
                    <h3>{product.brand} {product.name}</h3>
                </div>
                )}
            )}
        </div>

        {gender.length > 0 && 

        <div class="row my-5 gender">

            <h1>Step into Style:</h1>
            <h4>Men's and Women's Shoe Collection</h4>

            <div class="col-4 offset-2">
                <img src={process.env.PUBLIC_URL + gender[0].image_path}  class="img-fluid" alt="..."/>
                <a class="btn btn-outline-dark" href="category.html">Shop Men</a>
            </div>

            <div class="col-4">
                <img src={process.env.PUBLIC_URL + gender[1].image_path}  class="img-fluid" alt="..."/>
                <a class="btn btn-outline-dark" href="category.html">Shop Women</a>
            </div>

        </div>
        }

        <div class="row my-4 mx-3 product_line">

            <h1>Everyday Sneakers</h1>
            <div class="view-all">
                <a class="btn btn-outline-dark" href="category.html">view all</a>
            </div>

            {everydaySneakers.map(product => {
                return (
                <div class="col">
                    <a href="#"> <img src={process.env.PUBLIC_URL + product.image_path} class="img-fluid" alt="..."/> </a>
                    <h3>{product.brand} {product.name}</h3>
                </div>
                )}
            )}

        </div>


        <div class="row my-4 mx-3 product_line">

            <h1>For Runners</h1>
            <div class="view-all">
                <a class="btn btn-outline-dark" href="category.html">view all</a>
            </div>

            {forRunners.map(product => {
                return (
                <div class="col">
                    <a href="#"> <img src={process.env.PUBLIC_URL + product.image_path} class="img-fluid" alt="..."/> </a>
                    <h3>{product.brand} {product.name}</h3>
                </div>
                )}
            )}

        </div>

        <div class="row iconic">
            <h1>
                Always Iconic
            </h1>
            <div class="col-6 offset-3">
                <h5>Each step in a pair of these legendary footwear feels like a stride through the annals of style. Their design, a testament to 
                    innovation and craftsmanship, has left an indelible mark on culture.
                </h5>
            </div>

            <div class="col-12 p-0 d-flex justify-content-center align-items-center flex-column image">
            </div>

            <div class="col text-center">
                <a class="btn btn-outline-dark " href="category.html">View collection</a>
            </div>

        </div>



    </>
    )
}



export default Home;
