import {React,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadAllProducts,selectdata } from "../slices/homeSlice";


function Home() {

    const dispatch = useDispatch();
    const products = useSelector(selectdata);

    useEffect(() => {
        dispatch(loadAllProducts());
    }, 
    []);

    return(
        <div class="row head">

            <div class="taglines col-5 my-auto">
                <h1>Shop sneakers</h1>
                <p>Shop for the collest sneekers this autumn at the best available prices</p>
                <a class="btn btn-outline-dark" href="category.html">View collection</a>
            </div>

            <div class="col-7 my-auto">
                <img src = '`${products.image_url}`' class="img-fluid" alt="..."> </img>
            </div>

        </div>
    )
}



export default Home;
