import {React,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link,useParams,useLocation } from "react-router-dom";
import { loadProducts,selectdata } from "../slices/productsSlice";


function Products() {

    const {name} = useParams();
    const url = useLocation().pathname;
    const filter = url.split('/')[1];

    const dispatch = useDispatch();
    const products = useSelector(selectdata);
    console.log(products)


    useEffect(() => {
        dispatch(loadProducts({filter,name}));
    }, 
    []);

    return(
    <>
        <div class="row header">

            <div class="col text-center">
                <h1>
                    {name}
                </h1>
            </div>

            <div class="row  listings">


                {products.map(product => {
                    return (

                        <div class="col-4 product">
                            <a href="#"> <img src={process.env.PUBLIC_URL + product.image_path}class="img-fluid" alt="..."/> </a>
                            <a href="#"> class="product_name">Air jordan</a>
                            <span>$200</span>
                        </div>
                    )}
                )}

            </div>


        </div>

    </>
    )
}



export default Products;
