import {React,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadFeaturedProducts,selectdata } from "../slices/featuredSlice";
import Banner from "./banner";
import Featured from "./featured";


function Home() {

    const dispatch = useDispatch();
    const featuredProducts = useSelector(selectdata);
    console.log(featuredProducts)


    useEffect(() => {
        dispatch(loadFeaturedProducts());
    }, 
    []);

    return(
    <>
        <Banner/>

        <div className="row my-4 mx-3 product_line">

            <h1 id="featured">Featured</h1>

            {featuredProducts.map(product => {
                return (
                <div className="col-3">
                    <a href="#"> <img src={process.env.PUBLIC_URL + product.image_path} className="img-fluid" alt="..."/> </a>
                    <h3>Air jordan</h3>
                </div>
                )}
            )}
        </div>

    </>
    )
}



export default Home;
