import {React,useState} from "react";
import { useNavigate } from 'react-router-dom';

function Login(){

    const [username, setusername] = useState();
    const [password, setpassword] = useState();

    const [error, setError] = useState('')
    const navigate = useNavigate();

    async function handlesumbit(e) {
        e.preventDefault();
        const data = {username,password};

        try {
            const response = await fetch('http://localhost:4000/login',{
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Origin': 'http://localhost:3000'
                },
                body: JSON.stringify(data)
            })

            if (!response.ok) {
                console.log('response=',response)
                throw new Error('Server error');
            } else{
                //navigate({pathname: '/'})
            }
        } catch (error) {
            console.error(error);
            setError('An error has occurred. Please try again');
        }
    } 

    return (
    <>
        <div class="row header cart_checkout" id="login_heading">
            <div class="col text-center">
                <h1>Login</h1>
            </div>

        </div>

        <div class="row justify-content-center login_row ">

            <div class="col-5 login">

                <p class="fw-light">Don't have have an account? <a href="#"> Register </a> </p> 


                <div class="mb-4 text-center facebook">
                    <a class="btn btn-outline-dark text-decoration-none" href="category.html">Login with facebook</a>
                </div>

                <form>

                    <div class="form-outline mb-4">
                        <label class="form-label" for="loginUsername">Username</label>
                        <input type="text" id="loginUsername" class="form-control" />                      
                    </div>

                    <div class="form-outline mb-4">
                        <label class="form-label" for="loginUsername">Password</label>
                        <input type="text" id="loginUsername" class="form-control" />                       
                    </div>

                    <a class="btn btn-outline-dark px-5 border-1 text-decoration-none login_btn" type="submit" href="#">Log in</a>

                </form>

            </div>

        </div>
    </>
    )
}

export default Login
