import {React,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadorders,selectdata } from "../slices/orderSlice";
import { orderValue } from "../util/helperfunc";

function Profile(){

    const dispatch = useDispatch();
    const orders = useSelector(selectdata)

    useEffect(() => {
        dispatch(loadorders());
    }, []);

    function formatDate(date){
        const dateObject = new Date(date);
        const formattedDate = dateObject.toLocaleDateString();
        return formattedDate
    }

    return(
    <>
        <div class="row header cart_checkout" id="orders_heading">
            <div class="col text-center">
                <h1>Your orders</h1>
                <p class="fw-lighter mb-5"> Your orders in one place </p> 
            </div>

        </div>

        <div class="row justify-content-center orders_row">

            <div class="col-6 ">

                <table class="table table-borderless my-3 align-middle">

                    <thead class="table-light">
                        <tr>
                            <th scope="col">Order #</th>
                            <th scope="col">Date</th>
                            <th scope="col">Total</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>

                    <tbody>

                        {/* <tr>
                            <th scope="row">1</th>
                            <td>22/07/2023</td>
                            <td class="total_action">$250</td>
                            <td class="total_action"> <a class="btn btn-outline-dark px-3 py-1 border-1 text-decoration-none view_btn" data-bs-toggle="modal" data-bs-target="#orderDetailsModal" data-orderid="1" href="#">view</a> </td>
                        </tr> */}


                        {orders.map(order => {
                            return (
                                <tr>
                                    <th scope="row">{order.order_id}</th>
                                    <td style= {{width: '28%'}}>{formatDate(order.items[0].date)}</td>
                                    <td class="total_action">${orderValue(order.items)}</td>
                                    <td class="total_action"> <a class="btn btn-outline-dark px-3 py-1 border-1 text-decoration-none view_btn" data-bs-toggle="modal" data-bs-target="#orderDetailsModal" data-orderid="1" href="#">view</a> </td>
                                </tr>
                                )
                            })
                        }
                    </tbody>

                </table>

            </div>

        </div>

        <div class="row justify-content-center my-5">
            <div class="col-3">
                <a class="btn btn-outline-dark px-5 border-1 text-decoration-none login_btn" type="submit" href="#">Log out</a>
            </div>
        </div>

        { order.length === 0 && <div class="fill" style= {{marginTop: '20.5%'}}></div>}
        { cart.length === 1 && <div class="fill" style= {{marginTop: '8.5%'}}></div>}
        { cart.length >= 2 && <div class="fill" style= {{marginTop: '5%'}}></div>}
    </>
    )
}

export default Profile;
