const API_ENDPOINT = 'http://localhost:4000';

export async function getAllProducts(){

    const productList = await fetch(`${API_ENDPOINT}/`)
    console.log('products',productList)

    .then(async response => {
        const Data =  await response.json();
        console.log('data=',Data)
        return Data;
    })

    return productList
}
