import {React,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadFeaturedProducts,selectfeatured,selectgender,selecteverydaySneakers,selectforRunners } from "../slices/featuredSlice";
import Banner from "./banner";
import Featured from "./featured";


function Home() {

    const dispatch = useDispatch();
    const featuredProducts = useSelector(selectfeatured);
    const gender = useSelector(selectgender);
    console.log(gender)


    useEffect(() => {
        dispatch(loadFeaturedProducts());
    }, 
    []);

    return(
    <>
        <Banner/>

        <div className="row my-4 mx-3 product_line">

            <h1 id="featured">Featured</h1>

            {featuredProducts.map(product => {
                return (
                <div class="col">
                    <a href="#"> <img src={process.env.PUBLIC_URL + product.image_path} class="img-fluid" alt="..."/> </a>
                    <h3>Air jordan</h3>
                </div>
                )}
            )}
        </div>




    </>
    )
}



export default Home;
