const API_ENDPOINT = 'http://localhost:4000';

export async function getbannerProduct() {
    const response = await fetch(`${API_ENDPOINT}/products`)
    .then(async response =>{
        const data =  await response.json();
        const bannerProduct = data.find(product => product.id === 6);
        return bannerProduct
    })
    return response
}

export async function getHomeProducts() {
    const response = await fetch(`${API_ENDPOINT}/products`)
    .then(async response =>{
        const data =  await response.json();

        const featuredIds = [6, 9, 11, 15];
        const genderIds = [3,4];
        const everydaySneakersIds = [11,1,13,2];
        const forRunnersIds = [3,9,15,2]

        const featured = data.filter(product => featuredIds.includes(product.id));
        const gender = data.filter(product => genderIds.includes(product.id));
        const everydaySneakers = data.filter(product => everydaySneakersIds.includes(product.id));
        const forRunners = data.filter(product => forRunnersIds.includes(product.id));


        return [featured,gender,everydaySneakers,forRunners]
    })
    return response
}
