import { registerUser } from "../api/serverCalls";
const { createAsyncThunk, createSlice } =  require("@reduxjs/toolkit");


export const createUser = createAsyncThunk(
    'register/postData',
    async ({firstName,lastName,username,password}) => {
        const user = await registerUser(firstName,lastName,username,password);
        return user
    }
) 

export const registerSlice = createSlice({
    name: "register",
    initialState: {
        user: [],
        isLoading:false,
        hasError:false
    },

    reducers: {},

    extraReducers: {

        [createUser.pending]: (state, action) => {
            console.log('pending')
            state.isLoading = true;
            state.hasError = false;
        },

        [createUser.fulfilled]: (state, action) => {
            console.log('fulfiled')           
            state.user = action.payload;
            state.isLoading = false;
            state.hasError = false;
        },

        [createUser.rejected]: (state, action) => {
            console.log('failed')
            state.isLoading = false;
            state.hasError = true;
        }
    }
});


export const selectdata = (state) => state.register.user;

export const isLoadingData = (state) => state.register.isLoading;
export default registerSlice.reducer;
