import { getbannerProduct } from "../api/serverCalls";
const { createAsyncThunk, createSlice } =  require("@reduxjs/toolkit");


export const loadbannerProduct = createAsyncThunk(
    'home/getData',
    async (type) => {
        const data = await getbannerProduct();
        return data
    }
) 

export const homeSlice = createSlice({
    name: "home",
    initialState: {
        products: [],
        isLoading:false,
        hasError:false
    },

    reducers: {},

    extraReducers: {

        [loadAllProducts.pending]: (state, action) => {
            console.log('pending')
            state.isLoading = true;
            state.hasError = false;
        },

        [loadAllProducts.fulfilled]: (state, action) => {
            console.log('fulfiled')           
            state.products = action.payload;
            state.isLoading = false;
            state.hasError = false;
        },

        [loadAllProducts.rejected]: (state, action) => {
            console.log('failed')
            state.isLoading = false;
            state.hasError = true;
        }
    }
});


export const selectdata = (state) => state.home.products;

export const isLoadingData = (state) => state.home.isLoading;
export default homeSlice.reducer;
