import { configureStore } from "@reduxjs/toolkit";
import bannerSliceReducer from "../slices/bannerSlice";
import { HomeSliceReducer } from "../slices/homeslice";

export default configureStore({
    reducer: {
      banner: bannerSliceReducer,
    },
});
