const API_ENDPOINT = 'http://localhost:4000';

export async function getbannerProduct() {
    const response = await fetch(`${API_ENDPOINT}/products`)
    .then(async response =>{
        const data =  await response.json();
        const bannerProduct = data.find(product => product.id === 7);
        return bannerProduct
    })
    return response
}

export async function getfeaturedProducts() {
    const response = await fetch(`${API_ENDPOINT}/products`)
    .then(async response =>{
        const data =  await response.json();
        const featuredProductIds = [18, 8, 11, 15];
        const featuredProducts = data.filter(product => featuredProductIds.includes(product.id));

        return featuredProductIds
    })
    return response
}
