import { loggedIn } from "../api/serverCalls";
const { createAsyncThunk, createSlice } =  require("@reduxjs/toolkit");


export const logInstatus = createAsyncThunk(
    'loggedIn/status',
    async () => {
        const logstatus = await loggedIn();
        return logstatus
    }
) 

export const registerSlice = createSlice({
    name: "register",
    initialState: {
        user: false,
        isLoading:false,
        hasError:false
    },

    reducers: {},

    extraReducers: {

        [logInstatus.pending]: (state, action) => {
            console.log('pending')
            state.isLoading = true;
            state.hasError = false;
        },

        [logInstatus.fulfilled]: (state, action) => {
            console.log('fulfiled')           
            state.user = action.payload;
            state.isLoading = false;
            state.hasError = false;
        },

        [logInstatus.rejected]: (state, action) => {
            console.log('failed')
            state.isLoading = false;
            state.hasError = true;
        }
    }
});


export const selectdata = (state) => state.loginstatus.user;

export const isLoadingData = (state) => state.loginstatus.isLoading;
export default Slice.reducer;
